CREATE OR REPLACE FUNCTION panorama_leito_pck.obter_ocorrencias_perfil (cd_perfil_p bigint) RETURNS SETOF T_REGRA_PERFIL AS $body$
DECLARE


	nr_seq_regra_w	bigint;

	c_icone CURSOR FOR
	SELECT	cd_icone_panorama ie_icone
	from	regra_perm_panorama_icone
	where	nr_seq_regra = nr_seq_regra_w
	and		(nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '')
	
union all

	SELECT	somente_numero(a.vl_dominio)
	from	valor_dominio_v a
	where	a.cd_dominio = 7403
	and		coalesce(nr_seq_regra_w::text, '') = ''
	order by 1;

	linha_w			t_regra_perfil_row;

	
BEGIN

	select	max(a.nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_perm_panorama a
	where	a.cd_perfil = cd_perfil_p
	and exists (	SELECT 1
			from	regra_perm_panorama_icone x
			where	x.nr_seq_regra = a.nr_sequencia  LIMIT 1);

	if (coalesce(nr_seq_regra_w::text, '') = '') then -- se nao encontrou a regra para o perfil, busca uma regra sem perfil definido.
		select	max(a.nr_sequencia)
		into STRICT 	nr_seq_regra_w
		from	regra_perm_panorama a
		where	coalesce(a.cd_perfil::text, '') = ''
		and exists (	SELECT 1
					from	regra_perm_panorama_icone x
					where	x.nr_seq_regra = a.nr_sequencia  LIMIT 1);
	end if;

	for r_c_icone in c_icone loop
		linha_w.ie_icone	:= r_c_icone.ie_icone;
		RETURN NEXT linha_w;
	end loop;


	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
