CREATE OR REPLACE FUNCTION patientsearchfunctions.findpatientsphonetic ( pInSst text ,pInBirthL timestamp ,pInBirthH timestamp ,pInSex text ,pInCode text ,pInRNumL integer ,pInRNumH integer ) RETURNS PATIENTSEARCHFUNCTIONS.CURRESULT AS $body$
BEGIN
    EXECUTE 'TRUNCATE TABLE W_PESSOA_FISICA_SEARCH';

    INSERT INTO W_PESSOA_FISICA_SEARCH
      SELECT /*+INDEX(T, PESSOA_FISICA_PHONETIC_INDEX)*/ cd_pessoa_fisica, nm_pessoa_fisica, dt_nascimento, ie_sexo, cd_pessoa_mae, '' AS matchRange
      FROM PESSOA_FISICA T
      WHERE (coalesce(pInSst::text, '') = '' OR GERA_FONETICA(nm_pessoa_fisica, 'N') LIKE GERA_FONETICA(pInSst, 'S'))
      AND (coalesce(pInCode::text, '') = '' OR cd_pessoa_fisica LIKE pInCode || '%')
      AND (coalesce(pInBirthL::text, '') = '' OR dt_nascimento >= pInBirthL)
      AND (coalesce(pInBirthH::text, '') = '' OR dt_nascimento <= pInBirthH)
      AND (coalesce(pInSex::text, '') = '' OR ie_sexo = pInSex);

    RETURN patientsearchfunctions.fetchfromexisting(pInRNumL, pInRNumH);

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
