CREATE OR REPLACE FUNCTION pck_sepsis_bundle_report.get_presc_antibiotic (nr_atendimento_p gqa_protocolo_pac.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


      is_prescr         varchar(2) := 'N';
      dt_inicio_protocolo_w gqa_protocolo_etapa_pac.dt_inicio%type;

      -- 3653 - ICCA Sepsis - Prescription order Antibiotic
      c_antibiotic CURSOR FOR
         SELECT pma.dt_horario
           from prescr_mat_alteracao pma,
                prescr_material      pm,
                material             m
          where pm.nr_prescricao = pma.nr_prescricao
            and m.cd_material = pm.cd_material
            and (m.cd_medicamento IS NOT NULL AND m.cd_medicamento::text <> '')
            and pma.dt_horario > dt_inicio_protocolo_w
            and pma.nr_atendimento = nr_atendimento_p

union

         SELECT pma.dt_horario
           from prescr_mat_alteracao       pma,
                prescr_material            pm,
                cirurgia_v                 c,
                material                   m,
                evento_cirurgia_paciente   e,
                cirurgia_agente_anestesico caa,
                cirurgia_agente_anest_ocor cao
          where pm.nr_prescricao = pma.nr_prescricao
            and m.cd_material = pm.cd_material
            and e.nr_cirurgia = c.nr_cirurgia
            and caa.nr_cirurgia = c.nr_cirurgia
            and cao.nr_seq_cirur_agente = caa.nr_sequencia
            and caa.cd_material = m.cd_material
            and pma.dt_horario > dt_inicio_protocolo_w
            and pma.nr_atendimento = nr_atendimento_p;

      r_antibiotic c_antibiotic%rowtype;


BEGIN

      -- 3653 ICCA Sepsis - Prescription order Antibiotic
      dt_inicio_protocolo_w := pck_sepsis_bundle_report.get_dt_ini_protocol(nr_atendimento_p, current_setting('pck_sepsis_bundle_report.cd_etp_presc_at_w')::bigint, dt_inicio_protocolo_w);

      if (dt_inicio_protocolo_w IS NOT NULL AND dt_inicio_protocolo_w::text <> '') then
         open c_antibiotic;
         loop
            fetch c_antibiotic
               into r_antibiotic;
            EXIT WHEN NOT FOUND; /* apply on c_antibiotic */

            is_prescr := 'S';

         end loop;
         close c_antibiotic;
      end if;

      return(is_prescr);
   END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
