CREATE OR REPLACE FUNCTION pck_sepsis_bundle_report.get_seps_compl_blood (nr_atendimento_p gqa_protocolo_pac.nr_atendimento%type) RETURNS bigint AS $body$
DECLARE

      is_compliance         bigint := 0;
      dt_inicio_protocolo_w gqa_protocolo_etapa_pac.dt_inicio%type;
      -- 3349 ICCA Sepsis - Prescription order Blood Culture
      c_blood CURSOR FOR
         SELECT pp.dt_coleta
           from prescr_procedimento pp,
                prescr_medica       pm,
                procedimento        c,
                exame_laboratorio   d
          where pm.nr_prescricao = pp.nr_prescricao
            and c.cd_procedimento = pp.cd_procedimento
            and c.ie_origem_proced = pp.ie_origem_proced
            and d.nr_seq_exame = pp.nr_seq_exame
            and d.nr_seq_exame = pkg_report_data.get_exam( cd_ex_blood_culture , d.nr_seq_exame)
            --and upper(c.ds_procedimento_pesquisa) like '%BLOOD CULTURE%'
            and pp.dt_coleta between dt_inicio_protocolo_w and dt_inicio_protocolo_w + (1 / 24)
            and pm.nr_atendimento = nr_atendimento_p;

      r_blood c_blood%rowtype;

BEGIN

      -- 3349 ICCA Sepsis - Prescription order Blood Culture
      dt_inicio_protocolo_w := pck_sepsis_bundle_report.get_dt_ini_protocol(nr_atendimento_p, current_setting('pck_sepsis_bundle_report.cd_etp_blood_culture_w')::bigint, dt_inicio_protocolo_w);

      if (dt_inicio_protocolo_w IS NOT NULL AND dt_inicio_protocolo_w::text <> '') then
         open c_blood;
         loop
            fetch c_blood
               into r_blood;
            EXIT WHEN NOT FOUND; /* apply on c_blood */

            is_compliance := 1;

         end loop;
         close c_blood;
      end if;

      return(is_compliance);
   END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
