CREATE OR REPLACE PROCEDURE pcs_sup_gerar_plano_pck.gerar_info_planej ( cd_empresa_p bigint, nm_usuario_p text) AS $body$
BEGIN

	/* Gerar atributos de todos os materiais para todos estabelecimentos */

	delete
	from	w_valor_atributo
	where	nm_usuario = nm_usuario_p;

	CALL CALL pcs_sup_gerar_plano_pck.gerar_atributos_padroes(nm_usuario_p);

	CALL CALL pcs_sup_gerar_plano_pck.atualizar_atributos_planej(cd_empresa_p,nm_usuario_p);

	PERFORM set_config('pcs_sup_gerar_plano_pck.qt_registros_w', 0, false);
	commit;

	/* Fim da geração dos atributos */

	/* Gerar valores das fórmulas dos materiais/estabelecimentos de acordo com o segmento */

	delete
	from	w_valor_formula
	where	nm_usuario = nm_usuario_p;

	CALL CALL pcs_sup_gerar_plano_pck.atualizar_valor_form_planej(cd_empresa_p,nm_usuario_p);

	PERFORM set_config('pcs_sup_gerar_plano_pck.qt_registros_w', 0, false);
	commit;

	/* Fim da geração dos valores de fórmula */

	END;

$body$
