SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pde.enviar_integracao (NR_SEQ_PDE_LOG_P bigint, NM_USUARIO_P text) AS $body$
DECLARE

    DS_JSON_W PDE_LOG.DS_JSON%TYPE;

BEGIN
    SELECT PL.DS_JSON
      INTO STRICT DS_JSON_W
      FROM PDE_LOG PL
     WHERE PL.NR_SEQUENCIA = NR_SEQ_PDE_LOG_P
       AND (PL.DS_JSON IS NOT NULL AND PL.DS_JSON::text <> '')
       AND (PL.DT_JSON IS NOT NULL AND PL.DT_JSON::text <> '');

    DS_JSON_W := BIFROST.SEND_INTEGRATION_CONTENT('patient.data.export',
                                                  DS_JSON_W,
                                                  coalesce(NM_USUARIO_P,
                                                      WHEB_USUARIO_PCK.GET_NM_USUARIO));

    UPDATE PDE_LOG PL
       SET PL.DT_ENVIO       = clock_timestamp(),
           PL.NM_USUARIO     = coalesce(NM_USUARIO_P, PL.NM_USUARIO),
           PL.DT_ATUALIZACAO = clock_timestamp()
     WHERE PL.NR_SEQUENCIA = NR_SEQ_PDE_LOG_P;

    COMMIT;
  EXCEPTION
    WHEN OTHERS THEN
      CALL PDE.GRAVA_LOG_ERRO('ENVIAR.INTEGRACAO.ERROR',
                         'NR_SEQ_PDE_LOG_P: ' || NR_SEQ_PDE_LOG_P ||
                         ', NM_USUARIO_P: ' || NM_USUARIO_P,
                         SQLSTATE,
                         SQLERRM);
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pde.enviar_integracao (NR_SEQ_PDE_LOG_P bigint, NM_USUARIO_P text) FROM PUBLIC;
