CREATE OR REPLACE PROCEDURE pde.processa_dados_pde_main (NR_SEQ_PDE_MAIN_P bigint DEFAULT NULL) AS $body$
DECLARE


  L RECORD;

BEGIN
    UPDATE PDE_LOG PL
       SET PL.DT_JSON  = TO_DATE('01/01/1900', 'DD/MM/YYYY'),
           PL.DT_ENVIO = TO_DATE('01/01/1900', 'DD/MM/YYYY')
     WHERE (coalesce(NR_SEQ_PDE_MAIN_P, 0) = 0 OR
           PL.NR_SEQ_PDE_MAIN = NR_SEQ_PDE_MAIN_P)
       AND coalesce(PL.DT_ENVIO::text, '') = ''
       AND coalesce(PL.DT_JSON::text, '') = ''
       AND NOT EXISTS (SELECT 1
              FROM PESSOA_FISICA PF
             WHERE PF.CD_PESSOA_FISICA = PL.CD_PESSOA_FISICA);

    FOR L IN (SELECT PL.CD_PESSOA_FISICA
                FROM PDE_LOG PL
               WHERE (coalesce(NR_SEQ_PDE_MAIN_P, 0) = 0 OR
                     PL.NR_SEQ_PDE_MAIN = NR_SEQ_PDE_MAIN_P)
                 AND (PL.CD_PESSOA_FISICA IS NOT NULL AND PL.CD_PESSOA_FISICA::text <> '')
                 AND coalesce(PL.DT_JSON::text, '') = ''
                 AND coalesce(PL.DT_ENVIO::text, '') = ''
               GROUP BY PL.CD_PESSOA_FISICA
               ORDER BY PL.CD_PESSOA_FISICA) LOOP
      GERAR_JSON(L.CD_PESSOA_FISICA);
    END LOOP;
  END;

$body$
