CREATE OR REPLACE FUNCTION pep_gerar_resumo_pck.get_tr_html_justif ( ds_justificativa_p text) RETURNS varchar AS $body$
DECLARE

	
	ds_retorno_w		varchar(2000);
	ds_justificava_w	varchar(255);
					
	
BEGIN
	
	Select 	substr(obter_desc_expressao(292337,'Justificativa'),1,255)
	into STRICT    ds_justificava_w
	;
	
	
		ds_retorno_w :=	'<tr valign="topmargin">' || chr(13) ||
				'<td colspan="6" style="font-size:13px">' || '   '||ds_justificava_w||': ' || ds_justificativa_p || '</td>' || chr(13) ||
				'</tr>';
			
	return ds_retorno_w;
	end;
	

$body$
