CREATE OR REPLACE FUNCTION person_json_pck.get_allergy ( cd_pessoa_fisica_p text) RETURNS PHILIPS_JSON_LIST AS $body$
DECLARE

	
	json_allergy_w		philips_json;
	json_allergy_list_w	philips_json_list;
	
	C01 CURSOR FOR
		SELECT	*
		from	bft_allergy_v
		where	patient_id = cd_pessoa_fisica_p;
	
BEGIN
	json_allergy_list_w := philips_json_list();
	
	for r_c01 in c01 loop
		begin
		json_allergy_w :=  philips_json();
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'patientId', r_c01.patient_id);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'allergySequenceId', r_c01.allergy_sequence_id);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'allergyCode', r_c01.allergy_code);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'mnemonicDescription', r_c01.mnemonic_description);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'allergySeverity', r_c01.allergy_severity);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'allergyReaction', r_c01.allergy_reaction);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'foodAllergyIdentifier', r_c01.food_allergy_identifier);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'drugAllergyIdentifier', r_c01.drug_allergy_identifier);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'latexAllergyIdentifier', r_c01.latex_allergy_identifier);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'identificationDate', r_c01.identification_date);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'recordDate', r_c01.record_date);
		json_allergy_w := person_json_pck.add_json_value(json_allergy_w, 'allergyDescription', r_c01.allergy_description);
		json_allergy_list_w.append(json_allergy_w.to_json_value());
		
		end;
	end loop;
	
	
	return json_allergy_list_w;
	
	END;

$body$
