CREATE OR REPLACE PROCEDURE pfcs_log_pck.pfcs_insert_org_struc_log ( ie_type_p pfcs_org_structure_log.ie_type%type, nm_table_p pfcs_org_structure_log.nm_table%type, ds_log_p pfcs_org_structure_log.ds_log%type, nm_usuario_p pfcs_org_structure_log.nm_usuario%type) AS $body$
BEGIN
        delete from pfcs_org_structure_log
        where dt_atualizacao_nrec < (clock_timestamp() - interval '60 days');

        insert into
        pfcs_org_structure_log(
            NR_SEQUENCIA,
            NM_USUARIO_NREC,
            NM_USUARIO,
            DT_ATUALIZACAO_NREC,
            DT_ATUALIZACAO,
            IE_TYPE,
            NM_TABLE,
            DS_LOG
        ) values (
            nextval('pfcs_org_structure_log_seq'),
            nm_usuario_p,
            nm_usuario_p,
            clock_timestamp(),
            clock_timestamp(),
            ie_type_p,
            nm_table_p,
            ds_log_p
        );
    end;

$body$
