CREATE OR REPLACE FUNCTION pfcs_obs_contributor_pck.get_impact_description (vl_impact_p text) RETURNS varchar AS $body$
DECLARE

	ds_return_w	varchar(10);
	
BEGIN
		if (vl_impact_p IS NOT NULL AND vl_impact_p::text <> '') then
			if (vl_impact_p >= 0 and vl_impact_p <= 0.33) then
				ds_return_w := PFCS_PCK_CONSTANTS.DS_LOW_IMPACT;
			elsif (vl_impact_p > 0.33 and vl_impact_p <= 0.66) then
				ds_return_w := PFCS_PCK_CONSTANTS.DS_MEDIUM_IMPACT;
			elsif (vl_impact_p > 0.66 and vl_impact_p <= 1) then
				ds_return_w := PFCS_PCK_CONSTANTS.DS_HIGH_IMPACT;
			end if;
		end if;
		
		return ds_return_w;
	END;

$body$
