CREATE OR REPLACE FUNCTION pfcs_obs_contributor_pck.get_los_for_popover (dt_entrance_p timestamp, vl_pred_los_p bigint) RETURNS varchar AS $body$
DECLARE

		vl_popover_los_w varchar(10);
		vl_current_los_w bigint;
		vl_pred_los_w bigint;
		vl_total_los_w bigint;
	
BEGIN
		select ceil(clock_timestamp() - dt_entrance_p), ceil(vl_pred_los_p)
		into STRICT vl_current_los_w, vl_pred_los_w
		;
		vl_total_los_w := vl_current_los_w + vl_pred_los_w;
		vl_popover_los_w := vl_current_los_w || '/' || vl_total_los_w;
		return vl_popover_los_w;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
