SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pfcs_pck.pfcs_activate_records (nr_seq_indicator_p bigint, nr_seq_operational_level_p bigint, nm_usuario_p text) AS $body$
DECLARE

        qt_reg_w integer := 0;

BEGIN
        -- pfcs_panel_detail
        select	count(1)
        into STRICT    qt_reg_w
        from    pfcs_panel_detail
        where   nr_seq_indicator = nr_seq_indicator_p
        and	    nr_seq_operational_level = nr_seq_operational_level_p
        and	    ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE;

        if (qt_reg_w > 0) then
            delete FROM pfcs_detail_employee e
            where exists (
                SELECT 1
                from pfcs_panel_detail d
                where d.nr_sequencia = e.nr_seq_detail
                    and d.nr_seq_indicator = nr_seq_indicator_p
                    and d.nr_seq_operational_level = nr_seq_operational_level_p
                    and d.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);

            delete FROM pfcs_detail_exam_lab l
            where exists (
                SELECT 1
                from pfcs_panel_detail d
                where d.nr_sequencia = l.nr_seq_detail
                    and d.nr_seq_indicator = nr_seq_indicator_p
                    and d.nr_seq_operational_level = nr_seq_operational_level_p
                    and d.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);

            delete from pfcs_detail_bed b
            where exists (
                SELECT 1
                from pfcs_panel_detail d
                where d.nr_sequencia = b.nr_seq_detail
                    and d.nr_seq_indicator = nr_seq_indicator_p
                    and d.nr_seq_operational_level = nr_seq_operational_level_p
                    and d.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);

            delete from pfcs_detail_schedule s
            where exists (
                SELECT 1
                from pfcs_panel_detail d
                where d.nr_sequencia = s.nr_seq_detail
                    and d.nr_seq_indicator = nr_seq_indicator_p
                    and d.nr_seq_operational_level = nr_seq_operational_level_p
                    and d.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);

            delete from pfcs_detail_surgery s
            where exists (
                SELECT 1
                from pfcs_panel_detail d
                where d.nr_sequencia = s.nr_seq_detail
                    and d.nr_seq_indicator = nr_seq_indicator_p
                    and d.nr_seq_operational_level = nr_seq_operational_level_p
                    and d.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);

            delete from pfcs_detail_patient p
            where exists (
                SELECT 1
                from pfcs_panel_detail d
                where d.nr_sequencia = p.nr_seq_detail
                    and d.nr_seq_indicator = nr_seq_indicator_p
                    and d.nr_seq_operational_level = nr_seq_operational_level_p
                    and d.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);

            delete FROM pfcs_panel_detail a
            where a.nr_seq_indicator = nr_seq_indicator_p
                and a.nr_seq_operational_level = nr_seq_operational_level_p
                and	exists (
                    SELECT 1
                    from pfcs_panel x
                    where x.nr_sequencia = a.nr_seq_panel
                    and	x.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE);
        end if;

        update	pfcs_panel_detail
        set	    ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE,
                dt_atualizacao = clock_timestamp(),
                nm_usuario = nm_usuario_p
        where   nr_seq_indicator = nr_seq_indicator_p
        and	    nr_seq_operational_level = nr_seq_operational_level_p
        and	    ie_situation = PFCS_PCK_CONSTANTS.IE_TEMPORARY;

        -- pfcs_panel
        select  count(1)
        into STRICT    qt_reg_w
        from    pfcs_panel
        where   nr_seq_indicator = nr_seq_indicator_p
        and	    nr_seq_operational_level = nr_seq_operational_level_p
        and	    ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE;

        if (qt_reg_w > 0) then
            delete	FROM pfcs_panel
            where   nr_seq_indicator = nr_seq_indicator_p
            and	    nr_seq_operational_level = nr_seq_operational_level_p
            and	    ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE;
        end if;

        update	pfcs_panel
        set	    ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE,
                dt_atualizacao = clock_timestamp(),
                nm_usuario = nm_usuario_p
        where   nr_seq_indicator = nr_seq_indicator_p
        and	    nr_seq_operational_level = nr_seq_operational_level_p
        and	    ie_situation = PFCS_PCK_CONSTANTS.IE_TEMPORARY;

        commit;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pfcs_pck.pfcs_activate_records (nr_seq_indicator_p bigint, nr_seq_operational_level_p bigint, nm_usuario_p text) FROM PUBLIC;
