CREATE OR REPLACE FUNCTION pfcs_pck_census.get_anticipated_discharges (cd_establishment_p bigint, cd_unit_p bigint default null, cd_unit_classification_p text default null, dt_predicted_discharge_p timestamp DEFAULT NULL, ie_discharge_order_status_p text default null, ie_hospital_occupancy_p text default PFCS_PCK_CONSTANTS.IE_NO) RETURNS bigint AS $body$
DECLARE


        ds_timezone_w                   establishment_locale.ds_timezone%type;
        qt_anticipated_discharges_w     integer;
        dt_predicted_discharge_w        timestamp;

BEGIN
        ds_timezone_w := get_establishment_timezone(cd_establishment_p);
        dt_predicted_discharge_w := trunc(timezone_utils.getDate(dt_predicted_discharge_p,ds_timezone_w));

        select count(pd.nr_sequencia)
        into STRICT qt_anticipated_discharges_w
        from pfcs_panel_detail pd,
            pfcs_detail_patient pat,
            pfcs_detail_bed bed
        where pd.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE
            and pd.nr_seq_indicator = 100
            and pd.nr_seq_operational_level = cd_establishment_p
            and (coalesce(cd_unit_p::text, '') = '' or bed.cd_department = to_char(cd_unit_p))
            and (coalesce(cd_unit_classification_p::text, '') = '' or bed.ie_classification = cd_unit_classification_p)
            and trunc(timezone_utils.getDate(pat.dt_expected_discharge,ds_timezone_w)) = dt_predicted_discharge_w
            and (coalesce(ie_discharge_order_status_p::text, '') = '' or pat.ie_discharge_order_status = ie_discharge_order_status_p)
            and pat.nr_seq_detail = pd.nr_sequencia
            and bed.nr_seq_detail = pat.nr_seq_detail
            and (ie_hospital_occupancy_p = PFCS_PCK_CONSTANTS.IE_NO or
                    pfcs_pck_utils.get_ie_hospital_occupancy(isnumber(bed.cd_department)) = PFCS_PCK_CONSTANTS.IE_YES_BR);
        return qt_anticipated_discharges_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
