CREATE OR REPLACE FUNCTION pfcs_pck_census.get_discharge_orders ( cd_establishment_p bigint, cd_unit_p bigint, cd_unit_classification_p text, dt_period_start_p timestamp, dt_period_end_p timestamp, ie_over_threshold_p text) RETURNS bigint AS $body$
DECLARE

        qt_return_w integer;

BEGIN
        select count(pd.nr_sequencia)
        into STRICT qt_return_w
        from pfcs_panel_detail pd,
            pfcs_detail_patient pat,
            pfcs_detail_bed bed
        where pd.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE
            and pd.nr_seq_indicator = 100
            and pd.nr_seq_operational_level = cd_establishment_p
            and (coalesce(cd_unit_p::text, '') = '' or bed.cd_department = to_char(cd_unit_p))
            and (coalesce(cd_unit_classification_p::text, '') = '' or bed.ie_classification = cd_unit_classification_p)
            and pat.ie_discharge_order_status = 'Y'
            and ((coalesce(ie_over_threshold_p::text, '') = '') or (pat.ie_over_threshold = ie_over_threshold_p))
            and ((coalesce(dt_period_start_p::text, '') = '' and coalesce(dt_period_end_p::text, '') = '') or (pat.dt_request between dt_period_start_p and dt_period_end_p))
            and pat.nr_seq_detail = pd.nr_sequencia
            and bed.nr_seq_detail = pat.nr_seq_detail;
        return qt_return_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
