CREATE OR REPLACE FUNCTION pfcs_pck_communications.get_total_users (nr_seq_rule_p bigint, ie_communication_type_p text) RETURNS bigint AS $body$
DECLARE

        nr_total_users_w    bigint := 0;

BEGIN
        select  count(1)
          into STRICT  nr_total_users_w
          from  pfcs_communication_target pct
         where  pct.nr_seq_rule = nr_seq_rule_p
           and  (((ie_communication_type_p = pfcs_pck_constants.IE_SMS_SEND) and (pct.ie_communication_sms = pfcs_pck_constants.IE_YES_BR) and (pct.nr_celular IS NOT NULL AND pct.nr_celular::text <> ''))
            or  ((ie_communication_type_p = pfcs_pck_constants.IE_EMAIL_SEND) and (pct.ie_communication_email = pfcs_pck_constants.IE_YES_BR) and (pct.ds_destination_email IS NOT NULL AND pct.ds_destination_email::text <> ''))
            or  ((ie_communication_type_p = pfcs_pck_constants.IE_COMMUNICATION_INTERNAL) and (pct.ie_communication_internal = pfcs_pck_constants.IE_YES_BR) and (pct.ds_destination_user IS NOT NULL AND pct.ds_destination_user::text <> '')));
        return nr_total_users_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
