CREATE OR REPLACE FUNCTION pfcs_pck_discharge_manager.get_pr_discharges_by_noon (cd_establishment_p bigint) RETURNS bigint AS $body$
DECLARE

        qt_discharges_today_w integer;

BEGIN
        select sum(vl_indicator)
        into STRICT qt_discharges_today_w
        from pfcs_panel
        where nr_seq_indicator = 215
            and nr_seq_operational_level = cd_establishment_p;
        return pfcs_get_percentage_value(pfcs_pck_discharge_manager.get_discharges_by_noon(cd_establishment_p), coalesce(qt_discharges_today_w, 0));
    END;

$body$
LANGUAGE PLPGSQL
