CREATE OR REPLACE FUNCTION pfcs_pck_dynamic_cards.get_valid_type (cd_type_card_p text, cd_type_indicator_p text) RETURNS varchar AS $body$
BEGIN
        if (    (cd_type_card_p in (PFCS_PCK_CONSTANTS.CD_LEFT_CARD, PFCS_PCK_CONSTANTS.CD_BIG_NUMBER) and
                    cd_type_indicator_p in (
                        PFCS_PCK_INDICATORS.CD_TYPE_DECIMAL,
                        PFCS_PCK_INDICATORS.CD_TYPE_FRACTION,
                        PFCS_PCK_INDICATORS.CD_TYPE_NUMBER,
                        PFCS_PCK_INDICATORS.CD_TYPE_PERCENTAGE,
                        PFCS_PCK_INDICATORS.CD_TYPE_DATETIME,
                        PFCS_PCK_INDICATORS.CD_TYPE_TIME,
                        PFCS_PCK_INDICATORS.CD_TYPE_SIMULATION)
                )
                or (cd_type_card_p = PFCS_PCK_CONSTANTS.CD_DYNAMIC_CHART and cd_type_indicator_p = PFCS_PCK_INDICATORS.CD_TYPE_CHART)
                or (cd_type_card_p = PFCS_PCK_CONSTANTS.CD_WCPANEL and cd_type_indicator_p = PFCS_PCK_INDICATORS.CD_TYPE_WCPANEL)
                or (cd_type_card_p = PFCS_PCK_CONSTANTS.CD_SEGMENTED_CONTROL and
                    cd_type_indicator_p in (PFCS_PCK_INDICATORS.CD_TYPE_CHART, PFCS_PCK_INDICATORS.CD_TYPE_TABLECHART))
                or (cd_type_card_p = PFCS_PCK_CONSTANTS.CD_TABLECHART and cd_type_indicator_p = PFCS_PCK_INDICATORS.CD_TYPE_TABLECHART)
        ) then
            return PFCS_PCK_CONSTANTS.IE_YES;
        end if;

        return PFCS_PCK_CONSTANTS.IE_NO;
    END;

