SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pfcs_pck_emergency_department.get_queue_type_from_encounter (nr_seq_encounter_p bigint) RETURNS varchar AS $body$
DECLARE

		ds_type     pfcs_task.ds_reason_display%type;

	
BEGIN
		select MAX(tsk.ds_reason_display)
			into STRICT ds_type
		from pfcs_encounter enc
			left join pfcs_task tsk on tsk.nr_seq_encounter = enc.nr_sequencia
		where enc.nr_sequencia = nr_seq_encounter_p
			and tsk.cd_task_code = PFCS_PCK_CONSTANTS.CD_TASK_ED_QUEUE
		order by tsk.dt_atualizacao desc;

		return ds_type;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
