CREATE OR REPLACE FUNCTION pfcs_pck_indicators.get_detail_count ( nr_seq_indicator_p bigint, cd_establishment_p bigint, ds_department_p text default null) RETURNS bigint AS $body$
DECLARE

        vl_return_w             pfcs_panel.vl_indicator%type := 0;

BEGIN
        select  count(1)
          into STRICT  vl_return_w
          from  pfcs_panel_detail ppd
        inner join pfcs_detail_patient pdp on (ppd.nr_sequencia = pdp.nr_seq_detail)
         left join pfcs_detail_bed pdb ON (ppd.nr_sequencia = pdb.nr_seq_detail)
         where  ppd.nr_seq_indicator = nr_seq_indicator_p
           and  ppd.nr_seq_operational_level = cd_establishment_p
           and  ppd.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE
           and (coalesce(ds_department_p::text, '') = '' or pdb.cd_department = ds_department_p);

        return vl_return_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
