CREATE OR REPLACE FUNCTION pfcs_pck_indicators.get_type (nr_seq_indicator_p bigint) RETURNS varchar AS $body$
DECLARE

        cd_type_w pfcs_indicator.cd_type%type;

BEGIN
        select  case when max(cd_type) = current_setting('pfcs_pck_indicators.cd_type_simulation')::varchar(4)
                    then max(PFCS_PCK_CONSTANTS.CD_PREDICTED_PREFIX||cd_predicted_type)
                    else max(cd_type)
                end
        into STRICT    cd_type_w
        from    pfcs_indicator
        where   nr_sequencia = nr_seq_indicator_p;

        return cd_type_w;
    end;


