SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pfcs_pck_operating_rooms.calc_avg_assertiveness_index (cd_establishment_p bigint, nm_user_p text) AS $body$
DECLARE

        pfcs_panel_seq_w            pfcs_panel.nr_sequencia%type;
        vl_assertiveness_index_w    pfcs_panel.vl_indicator_dec%type;

BEGIN
        select avg(sch.vl_assertiveness_index) vl_assertiveness_index
        into STRICT vl_assertiveness_index_w
        from pfcs_detail_schedule sch,
            pfcs_panel_detail pd
        where sch.nr_seq_detail = pd.nr_sequencia
            and pd.nr_seq_indicator = PFCS_PCK_INDICATORS.NR_OR_PERFORMED_SCHEDULED
            and pd.ie_situation = PFCS_PCK_CONSTANTS.IE_ACTIVE
            and pd.nr_seq_operational_level = cd_establishment_p;

         := pfcs_pck.pfcs_generate_results(
            vl_indicator_p => null, vl_indicator_dec_p => vl_assertiveness_index_w, nr_seq_indicator_p => PFCS_PCK_INDICATORS.NR_OR_ASSERTIVENESS_INDEX, nr_seq_operational_level_p => cd_establishment_p, nm_usuario_p => nm_user_p, nr_seq_panel_p => pfcs_panel_seq_w);

        CALL pfcs_pck.pfcs_activate_records(
            nr_seq_indicator_p => PFCS_PCK_INDICATORS.NR_OR_ASSERTIVENESS_INDEX,
            nr_seq_operational_level_p => cd_establishment_p,
            nm_usuario_p => nm_user_p);
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
