SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pfcs_pck_sim.get_simulated_pacu_occupancy ( cd_establishment_p bigint, nr_hour_p bigint ) RETURNS bigint AS $body$
DECLARE

        nr_seq_observation_w    pfcs_observation.nr_sequencia%type;
        qt_patients_w           integer := 0;

BEGIN
        select max(nr_sequencia) into STRICT nr_seq_observation_w
        from (
            SELECT obs.nr_sequencia nr_sequencia
            from pfcs_observation obs,
                pfcs_encounter enc
            where obs.nr_seq_encounter = enc.nr_sequencia
                and upper(obs.cd_observation_type) = PFCS_PCK_CONSTANTS.CD_PACU_SIMULATION
                and pfcs_pck_utils.get_establishment_code(enc.nr_seq_organization) = cd_establishment_p
                and obs.dt_atualizacao > trunc(clock_timestamp() - interval '7 days')
            order by obs.dt_atualizacao desc
        ) v LIMIT 1;

        if (nr_seq_observation_w IS NOT NULL AND nr_seq_observation_w::text <> '') then
            select isnumber(substr(ds_value,20,10)) into STRICT qt_patients_w
            from pfcs_observ_extension
            where nr_seq_observation = nr_seq_observation_w
                and (trunc(clock_timestamp(), PFCS_PCK_CONSTANTS.CD_MASK_HH24) + (nr_hour_p/24)) = to_date(substr(ds_value, 1, 15), 'YYYY-MM-DD HH24:Mi:SS')
                and substr(upper(cd_identifier),1,5) = PFCS_PCK_CONSTANTS.CD_PACU_SIMULATION_TIME_PREFIX  LIMIT 1;
        end if;

        return qt_patients_w;
        exception
            when others then return 0;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
