CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_coverage_class_value (nr_seq_patient_p bigint, cd_type_p text) RETURNS varchar AS $body$
DECLARE

            ds_value_w varchar(255);

BEGIN
          select max(covclass.ds_value)
          into STRICT ds_value_w
          from pfcs_coverage_class covclass
          inner join pfcs_coverage cov
          on cov.nr_sequencia = covclass.nr_seq_coverage
          and cov.nr_seq_beneficiary_patient = nr_seq_patient_p
          and  covclass.cd_type_code = cd_type_p;

          return ds_value_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
