CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_dynamic_module_expression (nr_seq_module_p bigint) RETURNS varchar AS $body$
DECLARE

		ds_module_expression pfcs_dynamic_module.ds_dynamic_module%type;
	
BEGIN
		select substr(obter_desc_expressao(CD_EXP_SEGMENTED),1,255)
        into STRICT ds_module_expression
			from pfcs_dynamic_module
		where nr_sequencia = nr_seq_module_p;
        return ds_module_expression;
	END;

$body$
