CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_full_location (nr_seq_location_p bigint) RETURNS varchar AS $body$
DECLARE

        ds_location_w pfcs_detail_bed.ds_location%type;

BEGIN
        select (sec.ds_setor_atendimento || PFCS_PCK_CONSTANTS.DS_HYPHEN ||
                    uni.cd_unidade_basica || PFCS_PCK_CONSTANTS.DS_HYPHEN ||
                    uni.cd_unidade_compl) ds_location
        into STRICT    ds_location_w
        from    unidade_atendimento uni,
                setor_atendimento sec
        where   uni.cd_setor_atendimento = sec.cd_setor_atendimento
        and     uni.nr_seq_location = nr_seq_location_p;

        return ds_location_w;
    END;

$body$
