CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_if_surgical_room (nr_seq_location_p bigint) RETURNS varchar AS $body$
DECLARE

        ie_surgical_w unidade_atendimento.ie_exibir_cc%type;

BEGIN
        select  coalesce(max(PFCS_PCK_CONSTANTS.IE_YES),PFCS_PCK_CONSTANTS.IE_NO)
        into STRICT    ie_surgical_w
        from    unidade_atendimento uni
        where   uni.nr_seq_location = nr_seq_location_p
        and     uni.ie_exibir_cc = PFCS_PCK_CONSTANTS.IE_YES_BR;

        return ie_surgical_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
