CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_location_entrance_date (nr_seq_encounter_p bigint, nr_seq_location_p bigint) RETURNS timestamp AS $body$
DECLARE

        dt_entrance_w pfcs_encounter_location.period_start%type;

BEGIN
        select max(period_start)
        into STRICT dt_entrance_w
        from (
            SELECT loc.period_start
            from pfcs_encounter_location loc
            where loc.nr_seq_encounter = nr_seq_encounter_p
                and loc.nr_seq_location = nr_seq_location_p
