CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_object_column_name (nm_field_p text, nr_seq_object_p bigint) RETURNS varchar AS $body$
DECLARE

        ds_column_w dic_objeto.ds_texto%type;

BEGIN
        select  substr(obter_desc_expressao(cd_exp_campo_tela,nm_campo_tela),1,255)
        into STRICT    ds_column_w
        from    dic_objeto
        where   nr_seq_obj_sup = nr_seq_object_p
        and     nm_campo_base = nm_field_p;

        return ds_column_w;
    END;

