SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_pending_surgeries_title (cd_expressao_p bigint) RETURNS varchar AS $body$
DECLARE

        ds_title_w varchar(255);

BEGIN
      select replace(cd_expressao, regexp_replace(cd_expressao, '[^0-9]', ''), coalesce(max(qt_time_pending_surgeries),PFCS_PCK_CONSTANTS.VL_PENDING_SURGERIES_RULE))
      into STRICT ds_title_w
	  from (SELECT qt_time_pending_surgeries, obter_desc_expressao_idioma(cd_expressao_p, null, philips_param_pck.get_nr_seq_idioma) cd_expressao
      from pfcs_general_rule) config;

      return ds_title_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
