SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pfcs_pck_utils.get_postal_code (patient_id_p text) RETURNS varchar AS $body$
DECLARE

		ds_retorno_w  pfcs_address.postal_code%type;
	
BEGIN
		select  coalesce(postal_code,'') into STRICT ds_retorno_w
			from (SELECT a.postal_code
					from	pfcs_address a,
							pfcs_patient p
					where a.nr_seq_patient = p.nr_sequencia
					and p.patient_id = patient_id_p
					order by p.dt_atualizacao desc) alias1 LIMIT 1;
		return ds_retorno_w;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
