CREATE OR REPLACE PROCEDURE pfcs_pck_v2.pfcs_generate_history ( nr_seq_indicator_p bigint, nr_seq_operational_level_p bigint, nr_seq_panel_p bigint, nm_usuario_p text ) AS $body$
DECLARE

		nr_seq_panel_old_w	pfcs_panel.nr_sequencia%type;

	
BEGIN

		select	max(nr_sequencia)
		into STRICT	nr_seq_panel_old_w
		from	pfcs_panel
		where	nr_seq_indicator = nr_seq_indicator_p
		and	nr_seq_operational_level = nr_seq_operational_level_p
		and	ie_situation = 'A';

		if (nr_seq_panel_old_w > 0) then

			insert into pfcs_history(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_panel,
				dt_record,
				vl_indicator,
				vl_indicator_aux)
				SELECT	nextval('pfcs_history_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_panel_p,
					clock_timestamp(),
					vl_indicator,
					vl_indicator_aux
				from 	pfcs_panel
				where	nr_sequencia = nr_seq_panel_old_w;

			delete	FROM pfcs_history
			where	dt_record < clock_timestamp() - interval '43200 seconds';

			update	pfcs_history
			set	nr_seq_panel = nr_seq_panel_p,
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
			where	nr_seq_panel = nr_seq_panel_old_w;

		end if;

		commit;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
