SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pfcs_telemetry_config_pck.get_patient_review_value (cd_establishment_p bigint, cd_department_p text) RETURNS bigint AS $body$
DECLARE

		vl_patient_review_w		bigint := 0;
	
BEGIN
		-- Pacientes em telemetria > N h
		select	count(1) vl_indicator
		  into STRICT	vl_patient_review_w
		  from	pfcs_panel_detail ppd
		inner join pfcs_detail_patient pdp on (ppd.nr_sequencia = pdp.nr_seq_detail)
		 left join pfcs_detail_bed pdb ON (ppd.nr_sequencia = pdb.nr_seq_detail)
		 where	ppd.nr_seq_indicator = 46 -- Patient to review get data from this indicator
		   and	ppd.nr_seq_operational_level = cd_establishment_p
		   and (coalesce(cd_department_p::text, '') = '' or pdb.cd_department = cd_department_p)
		   and	ppd.nr_sequencia = pdb.nr_seq_detail
		   and	ppd.ie_situation = pfcs_pck_constants.IE_ACTIVE
		   and	pfcs_telemetry_config_pck.get_patient_review(pdp.dt_monitor_entrance) = pfcs_pck_constants.IE_YES_BR
		group by ppd.nr_seq_operational_level;

		return vl_patient_review_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
