BEGIN
		SELECT * FROM pfcs_telemetry_config_pck.telemetry_time_rules(current_setting('pfcs_telemetry_config_pck.qt_time_telemetry_rule_w')::pfcs_telemetry_config.qt_time_on_tele__rule%type, current_setting('pfcs_telemetry_config_pck.qt_trans_review_rule_w')::pfcs_telemetry_config.qt_trans_review_rule%type, current_setting('pfcs_telemetry_config_pck.qt_tr_trs_rule_w')::pfcs_telemetry_config.qt_tr_trs_rule%type, current_setting('pfcs_telemetry_config_pck.qt_tr_red_alrm_rule_w')::pfcs_telemetry_config.qt_tr_red_alrm_rule%type, current_setting('pfcs_telemetry_config_pck.qt_tr_yllw_alrm_rule_w')::pfcs_telemetry_config.qt_tr_yllw_alrm_rule%type) INTO STRICT current_setting('pfcs_telemetry_config_pck.qt_time_telemetry_rule_w')::pfcs_telemetry_config.qt_time_on_tele__rule%type, current_setting('pfcs_telemetry_config_pck.qt_trans_review_rule_w')::pfcs_telemetry_config.qt_trans_review_rule%type, current_setting('pfcs_telemetry_config_pck.qt_tr_trs_rule_w')::pfcs_telemetry_config.qt_tr_trs_rule%type, current_setting('pfcs_telemetry_config_pck.qt_tr_red_alrm_rule_w')::pfcs_telemetry_config.qt_tr_red_alrm_rule%type, current_setting('pfcs_telemetry_config_pck.qt_tr_yllw_alrm_rule_w')::pfcs_telemetry_config.qt_tr_yllw_alrm_rule%type;
		
