CREATE OR REPLACE FUNCTION pharmindex_pck.get_consulta (nr_seq_fila_p bigint) RETURNS SETOF T_CONSULTA AS $body$
DECLARE


r_consulta_w		r_consulta;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
nr_seq_processo_w	pharmindex_processo.nr_sequencia%type;
pharmindex_processo_w	pharmindex_processo%rowtype;


BEGIN
intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

begin
select	somente_numero(a.nr_seq_documento),
	b.ds_action
into STRICT	nr_seq_processo_w,
	reg_integracao_w.nm_elemento
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia		 = nr_seq_fila_p;
exception
when others then
	nr_seq_processo_w	:=	0;
end;

begin
select	*
into STRICT	pharmindex_processo_w
from	pharmindex_processo
where	nr_sequencia = nr_seq_processo_w;
exception
when others then
	pharmindex_processo_w	:=	null;
end;

reg_integracao_w.nm_tabela	:= 'PHARMINDEX_PROCESSO';

intpd_processar_atrib_envio(reg_integracao_w, 'DS_AUTENTICACAO', 'ClientAuthCode', 'N', pharmindex_processo_w.DS_AUTENTICACAO, 'N', r_consulta_w.ClientAuthCode);
intpd_processar_atrib_envio(reg_integracao_w, 'DT_INICIAL', 'ModifiedFrom', 'N', to_char(pharmindex_processo_w.DT_INICIAL,'yyyy-mm-dd'), 'N', r_consulta_w.ModifiedFrom);
intpd_processar_atrib_envio(reg_integracao_w, 'DT_FINAL', 'ModifiedTo', 'N', to_char(pharmindex_processo_w.DT_FINAL,'yyyy-mm-dd'), 'N', r_consulta_w.ModifiedTo);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_MATERIAL_EXTERNO', 'DrugId', 'N', pharmindex_processo_w.CD_MATERIAL_EXTERNO, 'N', r_consulta_w.DrugId);
RETURN NEXT r_consulta_w;

CALL intpd_gravar_log_fila(reg_integracao_w);
END;

