CREATE OR REPLACE FUNCTION phi_ordem_servico_pck.get_cd_gerente_sup (nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type) RETURNS PESSOA_FISICA.CD_PESSOA_FISICA%TYPE AS $body$
DECLARE

	
	cd_gerente_sup_w	pessoa_fisica.cd_pessoa_fisica%type;

	
BEGIN
	
	select	max(gw.cd_responsavel)
	into STRICT	cd_gerente_sup_w
	from	man_ordem_servico mos,
		grupo_suporte gs,
		gerencia_wheb gw
	where	mos.nr_seq_grupo_sup = gs.nr_sequencia
	and	gs.nr_seq_gerencia_sup = gw.nr_sequencia
	and	mos.nr_sequencia = nr_seq_ordem_serv_p;
	
	return cd_gerente_sup_w;

	END;

$body$
