CREATE OR REPLACE FUNCTION phi_ordem_servico_pck.get_ds_justificativa_vv ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


		ds_retorno_w	varchar(4000);

	
BEGIN

		select	substr(vv.ds_justificativa, 1, 4000)
		into STRICT	ds_retorno_w
		from	man_ordem_serv_analise_vv vv
		where	vv.nr_seq_ordem_serv 	= nr_seq_ordem_serv_p
		and 	coalesce(man_obter_dt_os_encerrada(vv.nr_seq_ordem_serv)::text, '') = '';

	return ds_retorno_w;
	END;

$body$
