CREATE OR REPLACE FUNCTION phi_ordem_servico_pck.get_info_service_order ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, nr_seq_idioma_p idioma.nr_sequencia%type default null) RETURNS SETOF T_MOS_ROW_DATA AS $body$
DECLARE


	t_mos_row_w		t_mos_row;
	nr_seq_idioma_w		idioma.nr_sequencia%type;

	c01 CURSOR(
		nr_seq_ordem_serv_cw	man_ordem_servico.nr_sequencia%type,
		nr_seq_idioma_cw	idioma.nr_sequencia%type) FOR
		SELECT	phi_ordem_servico_pck.get_ds_status(mos.nr_sequencia, nr_seq_idioma_cw) ds_status,
			phi_ordem_servico_pck.get_ds_classificacao(mos.nr_sequencia, nr_seq_idioma_cw) ds_classificacao,
			phi_ordem_servico_pck.get_ds_estagio(mos.nr_sequencia, nr_seq_idioma_cw) ds_estagio,
			phi_ordem_servico_pck.get_ds_dano_breve(mos.nr_sequencia) ds_dano_breve,
			phi_ordem_servico_pck.get_nr_versao_cliente_abertura(mos.nr_sequencia) nr_versao_cliente_abertura,
			phi_ordem_servico_pck.get_ds_severidade_wheb(mos.nr_sequencia, nr_seq_idioma_cw) ds_severidade_wheb
		from	man_ordem_servico mos
		where	mos.nr_sequencia = nr_seq_ordem_serv_cw;

	
BEGIN

		nr_seq_idioma_w := coalesce(nr_seq_idioma_p, wheb_usuario_pck.get_nr_seq_idioma, 1);

		for reg in c01(nr_seq_ordem_serv_p, nr_seq_idioma_w)
		loop
			begin

				t_mos_row_w.ds_status			:= reg.ds_status;
				t_mos_row_w.ds_classificacao		:= reg.ds_classificacao;
				t_mos_row_w.ds_estagio			:= reg.ds_estagio;
				t_mos_row_w.ds_dano_breve		:= reg.ds_dano_breve;
				t_mos_row_w.ds_severidade_wheb		:= reg.ds_severidade_wheb;
				t_mos_row_w.nr_versao_cliente_abertura	:= reg.nr_versao_cliente_abertura;

			RETURN NEXT t_mos_row_w;
			end;
		end loop;
	return;
	END;

$body$
