CREATE OR REPLACE FUNCTION phi_ordem_servico_pck.get_last_test_hist_date ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, ie_tipo_teste_p text) RETURNS timestamp AS $body$
DECLARE

	
	dt_liberacao_hist_w	man_ordem_serv_tecnico.dt_liberacao%type;
	nr_seq_tipo_hist_w	man_tipo_hist.nr_sequencia%type;

	
BEGIN
	
	if (ie_tipo_teste_p = 'P') then
		nr_seq_tipo_hist_w := 12;	--Unit test/Programmer test
	elsif (ie_tipo_teste_p = 'A') then
		nr_seq_tipo_hist_w := 31;	--Integration test/Analyst test
	end if;

	select	max(most.dt_liberacao)
	into STRICT	dt_liberacao_hist_w
	from	man_ordem_serv_tecnico most
	where	most.nr_seq_tipo = nr_seq_tipo_hist_w
	and	most.nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and	(most.dt_liberacao IS NOT NULL AND most.dt_liberacao::text <> '')
	and	(	(ie_tipo_teste_p = 'A')
		or	not exists (SELECT	1
					from	man_ordem_serv_tecnico mostx
					where	mostx.nr_seq_ordem_serv = most.nr_seq_ordem_serv
					and	mostx.nr_seq_tipo = 147 -- Analyst test - SO rejected
					and	mostx.dt_liberacao >= most.dt_liberacao));

	return dt_liberacao_hist_w;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
