CREATE OR REPLACE FUNCTION phi_ordem_servico_pck.get_superior_os (nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type) RETURNS MAN_ORDEM_SERVICO.NR_SEQUENCIA%TYPE AS $body$
DECLARE

	
	nr_seq_ordem_serv_superior_w	man_ordem_servico.nr_sequencia%type;
	nr_seq_proj_w			proj_projeto.nr_sequencia%type;
	
	
BEGIN
		
		select	max(mos.nr_seq_ordem_serv_pai)
		into STRICT	nr_seq_ordem_serv_superior_w
		from	man_ordem_servico mos
		where	mos.nr_sequencia = nr_seq_ordem_serv_p;
		
		if (coalesce(nr_seq_ordem_serv_superior_w::text, '') = '') then
			begin
				select	max(proj_obter_projeto_ordem_serv(nr_seq_ordem_serv_p))
				into STRICT	nr_seq_proj_w
				;
				
				select	max(proj_obter_dados_projeto(nr_seq_proj_w, 'OS'))
				into STRICT	nr_seq_ordem_serv_superior_w
				;
			end;
		end if;
		
		if (coalesce(nr_seq_ordem_serv_superior_w::text, '') = '') then
			begin
				nr_seq_ordem_serv_superior_w := nr_seq_ordem_serv_p;
			end;
		end if;
	
		return nr_seq_ordem_serv_superior_w;
		
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
