CREATE OR REPLACE PROCEDURE philips_business_log_pck.check_if_should_log () AS $body$
BEGIN

	RAISE NOTICE 'Ie_grava_log_w %', current_setting('philips_business_log_pck.ie_grava_log_w')::varchar(1);
	if current_setting('philips_business_log_pck.ie_grava_log_w')::coalesce(varchar(1)::text, '') = '' then
		select 	coalesce(max(ie_gravar_log), 'N')
		into STRICT	current_setting('philips_business_log_pck.ie_grava_log_w')::varchar(1)
		from	parametro_faturamento
		where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
	end if;	

END;

$body$
