CREATE OR REPLACE PROCEDURE philips_business_log_pck.write_log (cd_log_p text, ds_log_p text) AS $body$
DECLARE


nr_seq_proc_log_w	bigint;


BEGIN

	CALL philips_business_log_pck.check_if_should_log();
	if current_setting('philips_business_log_pck.ie_grava_log_w')::varchar(1) = 'S' then
		select 	nextval('log_proc_faturamento_seq')
		into STRICT	nr_seq_proc_log_w
		;
		
		insert into log_proc_faturamento(nr_sequencia, cd_funcao, cd_estabelecimento, dt_log, nm_usuario, ds_log, cd_tipo_evento)
		values (nr_seq_proc_log_w, obter_funcao_ativa, wheb_usuario_pck.get_cd_estabelecimento,
		clock_timestamp(), wheb_usuario_pck.get_nm_usuario, ds_log_p, cd_log_p);
		commit;
	end if;

END;

$body$
