CREATE OR REPLACE FUNCTION philips_contabil_pck.get_movto_inconsistencia ( nr_lote_contabil_p lote_contabil.nr_lote_contabil%type) RETURNS varchar AS $body$
DECLARE


		quant_w 		smallint;

		ie_return_w     varchar(1);

		
BEGIN
			select coalesce(max('S'),'N')
			into STRICT ie_return_w
			
			where exists (
				SELECT 1
				from ctb_movimento a
				where a.nr_lote_contabil = nr_lote_contabil_p
				and (a.ds_consistencia IS NOT NULL AND a.ds_consistencia::text <> ''));
		return ie_return_w;

		END;

$body$
