CREATE OR REPLACE FUNCTION philips_contabil_pck.get_tem_regra_ordenacao (cd_tipo_lote_contabil_p bigint) RETURNS varchar AS $body$
DECLARE


	ie_retorno_w		varchar(1)	:= 'N';
	cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
	
BEGIN
	cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

	if (cd_estabelecimento_w <> 0) then
		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	ctb_regra_agrup
		where	cd_estabelecimento	= cd_estabelecimento_w
		and	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p;
	end if;
	return ie_retorno_w;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
