CREATE OR REPLACE PROCEDURE philips_contabil_pck.inicializar_contabilizacao (cd_tipo_lote_contabil_p lote_contabil.cd_tipo_lote_contabil%type) AS $body$
DECLARE

	cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
	
BEGIN
		cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

		if (cd_estabelecimento_w <> 0) then
			select	coalesce(max(ie_regra_ordem_01),'XX'),
				coalesce(max(ie_regra_ordem_02),'XX'),
				coalesce(max(ie_regra_ordem_03),'XX'),
				coalesce(max(ie_regra_ordem_04),'XX'),
				coalesce(max(ie_regra_ordem_05),'XX')
			into STRICT	current_setting('philips_contabil_pck.ie_ordem_01_w')::ctb_regra_agrup.ie_regra_ordem_01%type,
				current_setting('philips_contabil_pck.ie_ordem_02_w')::ctb_regra_agrup.ie_regra_ordem_02%type,
				current_setting('philips_contabil_pck.ie_ordem_03_w')::ctb_regra_agrup.ie_regra_ordem_03%type,
				current_setting('philips_contabil_pck.ie_ordem_04_w')::ctb_regra_agrup.ie_regra_ordem_04%type,
				current_setting('philips_contabil_pck.ie_ordem_05_w')::ctb_regra_agrup.ie_regra_ordem_05%type
			from	ctb_regra_agrup
			where	cd_estabelecimento	= cd_estabelecimento_w
			and	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p;
		end if;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
