CREATE OR REPLACE FUNCTION philips_contabil_pck.obter_tipo_grupo_conta ( cd_conta_contabil_p conta_contabil.cd_conta_contabil%type) RETURNS varchar AS $body$
DECLARE


		ie_tipo_grupo_w	conta_contabil.ie_tipo%type;

		
BEGIN

		select	coalesce(max(a.ie_tipo),'X')
		into STRICT	ie_tipo_grupo_w
		from	conta_contabil b,
				ctb_grupo_conta a
		where	a.cd_grupo = b.cd_grupo
		and		b.cd_conta_contabil = cd_conta_contabil_p;

		return ie_tipo_grupo_w;

		END;

$body$
