CREATE OR REPLACE PROCEDURE philips_json_ext.pp_htp (obj philips_json, v_path text) AS $body$
DECLARE

    json_part philips_json_value;

BEGIN
    json_part := philips_json_ext.get_json_value(obj, v_path);
