SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE philips_json_printer.htp_output_clob (my_clob text, jsonp text default null) AS $body$
DECLARE


    l_amt    bigint := 30;
    l_off   bigint := 1;
    l_str   varchar(4096);


BEGIN
    if (jsonp IS NOT NULL AND jsonp::text <> '') then htp.prn(jsonp||'('); end if;

    begin
      loop
        dbms_lob.read( my_clob, l_amt, l_off, l_str );

        htp.prn( l_str  );
        l_off := l_off+l_amt;
        l_amt := 4096;
      end loop;
    exception
      when no_data_found then NULL;
    end;

    if (jsonp IS NOT NULL AND jsonp::text <> '') then htp.prn(')'); end if;
  end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
