CREATE OR REPLACE PROCEDURE philips_patrimonio_pck.calcular_deprec_empresa ( dt_referencia_p timestamp, cd_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w			bigint;
nm_estabelecimento_w			varchar(255);
ie_calcular_valor_transf_w		varchar(1)	:= 'N';

c01 CURSOR FOR
	SELECT	b.cd_estabelecimento,
		substr(obter_nome_estabelecimento(a.cd_estabelecimento),1,255)
	from	estabelecimento b,
		pat_mes_ref a
	where	a.cd_estabelecimento	= b.cd_estabelecimento
	and	b.cd_empresa		= cd_empresa_p
	and	a.ie_situacao		= 0
	and	a.dt_mes_ref		= dt_referencia_p;

c02 CURSOR FOR
	SELECT	b.cd_estabelecimento
	from	estabelecimento b,
		pat_mes_ref a
	where	a.cd_estabelecimento	= b.cd_estabelecimento
	and	b.cd_empresa		= cd_empresa_p
	and	a.ie_situacao		= 1
	and	a.dt_mes_ref		= dt_referencia_p;


BEGIN
open C01;
loop
fetch C01 into
	cd_estabelecimento_w,
	nm_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL gravar_processo_longo('Calculando depreciacao - Estabelecimento: ' || nm_estabelecimento_w, 'PHILIPS_PATRIMONIO_PCK.CALCULAR_DEPRECIACAO()', 0);

	CALL philips_patrimonio_pck.calcular_depreciacao(dt_referencia_p, cd_estabelecimento_w, nm_usuario_p);

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	ie_calcular_valor_transf_w := coalesce(obter_valor_param_usuario(937,48,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_w),'N');
	if (ie_calcular_valor_transf_w = 'S') then
		begin
		pat_atualizar_transf_bem( cd_estabelecimento_w, dt_referencia_p, nm_usuario_p);
		end;
	end if;
	end;
end loop;
close C02;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
