CREATE OR REPLACE PROCEDURE philips_patrimonio_pck.desfazer_deprec_empresa ( dt_referencia_p timestamp, cd_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	b.cd_estabelecimento,
	substr(obter_nome_estabelecimento(a.cd_estabelecimento),1,255) nm_estabelecimento
from	estabelecimento b,
	pat_mes_ref a
where	a.cd_estabelecimento	= b.cd_estabelecimento
and	b.cd_empresa		= cd_empresa_p
and	a.ie_situacao		= 1
and	a.dt_mes_ref		= dt_referencia_p;

c01_w		c01%rowtype;


BEGIN
open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	CALL philips_patrimonio_pck.consistir_desfazer_depreciacao(nm_usuario_p,c01_w.cd_estabelecimento,dt_referencia_p);
	end;
end loop;
close c01;

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	CALL gravar_processo_longo(wheb_mensagem_pck.get_texto(798594) || ' ' || c01_w.nm_estabelecimento, 'PHILIPS_PATRIMONIO_PCK.DESFAZER_DEPRECIACAO()', 0);
	CALL philips_patrimonio_pck.desfazer_depreciacao(dt_referencia_p, c01_w.cd_estabelecimento, nm_usuario_p);
	end;
end loop;
close c01;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
